var array_equals = require('array-equal');

module.exports = function(a1, a2){
  if(!a1 || !a2)
    return false;

  if(a1.dtype !== a2.dtype)
    return false;

  if(!array_equals(a1.data, a2.data))
    return false;

  if(!array_equals(a1.shape, a2.shape))
    return false;

  if(!array_equals(a1.stride, a2.stride))
    return false;

  if(a1.offset != a2.offset)
    return false;

  return true;
};
