var test = require('tape');
var ndarray = require('ndarray');
var equals = require('../');

test('ndarray comparison tests', function(t){
  var a1 = ndarray(new Float32Array([0, 1]));
  var a2 = ndarray(new Float32Array([0, 1]));
  t.assert(equals(a1, a2));

  a2 = ndarray([0, 1]);
  t.assert(!equals(a1, a2));

  a2 = ndarray(new Float64Array([0, 1]));
  t.assert(!equals(a1, a2));

  a1 = ndarray([0, 1, 2, 3], [2, 2]);
  a2 = ndarray([0, 1, 2, 3], [2, 2]);
  t.assert(equals(a1, a2));

  a2 = ndarray([0, 1, 2, 3]);
  t.assert(!equals(a1, a2));
  a2 = undefined;

  t.assert(!equals(a1, a2));
  t.end();
});
