var ndarray = require('ndarray');

module.exports.interpolate = function(arg){
  var step = arg[2];
  var base = arg[0];
  var length = Math.round( (arg[1] - arg[0]) / arg[2]) + 1;
  var arr = ndarray(new Float32Array(length));

  for(var i = 0; i <= length; i++){
    arr.set(i, base + i * step); 
  }

  return arr;
};
